jQuery( document ).ready( function() {
  var templateDirectory = amb_theme_directory.templateUrl;
  var origPlace = jQuery( '#wp-reading-order-dropdown' ).val();
  
  jQuery( '#wp-reading-order-dropdown' ).change( function () {
    var ID = jQuery( '#post_ID' ).val();
    var updatedPlace = jQuery( this ).val();
    jQuery( this ).removeClass( 'amb-saved' );
    jQuery.ajax( {
      'type': 'POST',
      'url': templateDirectory + '/php/save-list.php',
      'data': 'list=fut_admin&id=' + ID + '&place=' + updatedPlace + '&orig=' + origPlace,
      'success': function(data) {
        // data variable is anything echoed in above php file.
        jQuery( '#loading-container' ).toggle();
        origPlace = jQuery( '#wp-reading-order-dropdown' ).val();
        jQuery( '#wp-reading-order-dropdown' ).addClass( 'amb-saved' );
        if (data.length) {
          alert(data);
        }
      }
    } );
  } );
} );
